import { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = (props) => {
  const[title, setEnteredTitle] = useState('');
  const [amount, setEnteredAmount] = useState('');
const [date, setEnteredDate] = useState('');
  const titleChangedHandler = (event) => {
     setEnteredTitle(event.target.value);
  };
  const amountChangedHandler = (event) => {
    setEnteredAmount(event.target.value);
 };
 const dateChangedHandler = (event) => {
  setEnteredDate(event.target.value);
};
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangedHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={dateChangedHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="date" min="2019-01-01" max="2022" onChange={amountChangedHandler} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">New Expense</button>
      </div>
    </form>
  );
};
export default ExpenseForm;
